-- Contar precios insertados
SELECT COUNT(*) as total_precios FROM appinventory_productprice;

-- Productos con precios por tipo
SELECT 
    pt.name as tipo_precio,
    COUNT(pp.id) as total_precios,
    ROUND(AVG(pp.price), 2) as precio_promedio,
    ROUND(MIN(pp.price), 2) as precio_minimo,
    ROUND(MAX(pp.price), 2) as precio_maximo
FROM appinventory_productprice pp
JOIN appinventory_pricetype pt ON pp.price_type_id = pt.id
GROUP BY pt.name
ORDER BY pt.name;

-- Productos sin precios
SELECT 
    COUNT(*) as productos_sin_precio
FROM appinventory_product p
LEFT JOIN appinventory_productprice pp ON p.id = pp.product_id
WHERE pp.product_id IS NULL;

-- Ejemplos de precios por producto
SELECT 
    p.sku,
    p.name,
    pt.name as tipo_precio,
    pp.price,
    u.code as unidad,
    pp.is_default
FROM appinventory_product p
JOIN appinventory_productprice pp ON p.id = pp.product_id
JOIN appinventory_pricetype pt ON pp.price_type_id = pt.id
JOIN appinventory_unitofmeasure u ON pp.unit_id = u.id
WHERE p.sku LIKE '%THHN%'
ORDER BY p.sku, pt.name
LIMIT 20;
